// ESERCIZIO 1 : Prints a message that changes depending on user's age.
function checkAge() {
    const myAge = 32;
    let message = ''; //Message has to be a let.

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }

    return message; // Message has to be returned
}
let showMessage = checkAge();
console.log(showMessage);

// ESERCIZIO 2: Returns the amount of elements inside colors array
function printColorsNumber() {
    const colors = ['blue', 'red', 'yellow', 'green', 'black'];
    console.log(`Nella mia palette ci sono ${colors.length} colori!`);
    return colors.length; // Has to return the length of the array
}
printColorsNumber();


// ESERCIZIO 3: Asks user a number and sums it to another number.
function addNumbers() {
    const userNumber = parseInt(prompt('Inserisci un numero')); //Needs a parseint.
    const total = userNumber + 12;

    console.log(`Il risultato finale è ${total}`);
    return total;
}
addNumbers();


// ESERCIZIO 4: Asks user their mail and grants access only if it's in the addresses array
function checkAccess() {
    const addresses = ['mymail@mail.com', 'yourmail@mail.com', 'hermail@mail.com', 'hismail@mail.com'];
    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = 'false';

    if (addresses.includes(userEmail)) {
        grantAccess = 'true';
    }

    if (grantAccess === 'true') { //Has to be string
        console.log('Accesso consentito!');
    } else {
        console.log('Accesso negato!');
    }
}
checkAccess();


// ESERCIZIO 5 (suggerimento: c'è un solo errore)
function checkAccessImproved() { //We need to close this curly bracket
    const addresses = ['mymail@mail.com', 'yourmail@mail.com', 'hermail@mail.com', 'hismail@mail.com'];

    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = 'false';

    for (let i = 0; i < addresses.length; i++) {
        const email = addresses[i];

        if (userEmail.length > 5) {

            if (email === userEmail) {
                grantAccess = 'true';

            }

        }

        if (grantAccess) {
            console.log('Accesso consentito!');
        } else {
            console.log('Accesso negato!');
        }
    }
    checkAccessImproved();





























}